import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { StyleSheet, Text, View } from 'react-native';
import Icon from 'react-native-vector-icons/MaterialIcons';
import {LinearGradient} from 'expo-linear-gradient'

const alertIcon= <Icon name="warning" size={30} color="red"/>

export default function WorldStatisticsScreen() {
  return (
    <View style={styles.container}>
        
     <LinearGradient colors={['rgba(220, 190, 150,0.8)', 'transparent']} style={styles.linearGradient}>   
    <View style={styles.headingView}> 
        {alertIcon}
        <Text style={styles.heading}>COVID-19 STATISTICS</Text>
    </View>
    </LinearGradient>
    
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#271f1f',
    alignItems: 'center',
    justifyContent: 'flex-start',
  },
  headingView:{
      
      alignItems:'center',
      justifyContent:'center',
      width:'100%',
      flexDirection:'row',
      borderBottomColor:'#353839',
      borderBottomWidth:10,
      borderBottomLeftRadius:10,
      borderBottomRightRadius:10
  },
  heading:{
      fontSize:25,
      color:'white',
      fontWeight:'bold',
      margin:5
  },
  linearGradient:{
      flex:0.25,
      width:'100%',
      paddingTop:20

  }

});
